import { Todo } from "./interface.type";
import {
  GraphQLObjectType,
  GraphQLString,
  GraphQLBoolean,
  GraphQLSchema,
  GraphQLList
} from "graphql";
import { generateUniqueID, findIDAndDelete } from "./helpler";
export let todoMocking = [
  {
    id: "1",
    task: "Cleaning",
    done: false
  },
  {
    id: "2",
    task: "Writing",
    done: false
  },
  {
    id: "3",
    task: "Reading",
    done: false
  }
];

const TodoType = new GraphQLObjectType({
  name: "Todo",
  fields: () => ({
    id: { type: GraphQLString },
    task: { type: GraphQLString },
    done: { type: GraphQLBoolean }
  })
});

const queryType = new GraphQLObjectType({
  name: "Query",
  fields: () => ({
    allTodos: {
      type: new GraphQLList(TodoType),
      resolve() {
        return todoMocking;
      }
    },
    todo: {
      type: TodoType,
      args: { id: { type: GraphQLString } },
      resolve(parentValue, args, context, info) {
        return todoMocking.find(todo => todo.id === args.id);
      }
    }
  })
});

const DeletedResponseType = new GraphQLObjectType({
  name: "DeletedResponse",
  fields: () => ({
    message: { type: GraphQLString },
    isDelete: { type: GraphQLBoolean }
  })
});

const rootMutation = new GraphQLObjectType({
  name: "Mutation",
  fields: {
    addTodo: {
      type: TodoType,
      args: {
        task: { type: GraphQLString },
        done: { type: GraphQLBoolean }
      },
      resolve(parentValue, { task, done }) {
        const generatedID = generateUniqueID();
        const newTodo: Todo = { id: generatedID, task, done };
        todoMocking.push(newTodo);
        return newTodo;
      }
    },
    removeTodo: {
      type: DeletedResponseType,
      args: { id: { type: GraphQLString } },
      resolve(parentValue, { id }) {
        const isIDExist = todoMocking.find(todo => todo.id === id);
        if (isIDExist === undefined) {
          return { message: "TODO ID does not exists!", isDelete: false };
        }
        const newTodo = findIDAndDelete(id, todoMocking);
        todoMocking = newTodo;
        return { message: "Delete Successed", isDelete: true };
      }
    }
  }
});

const rootSchema = new GraphQLSchema({
  query: queryType,
  mutation: rootMutation
});

export { rootSchema };
